import axios from 'axios';

// получае новости из бы
export const addNews = () => (dispatch) =>{
    axios
    .get('/api/articles')
    .then(res=> {
        dispatch({
            type: 'ADD_NEWS',
            payload: res.data,
            loaded: true 
        });
    })
    .catch(err =>
        console.log(err))
 };

export const viewNews = (id, elem) => (dispatch) =>{


    axios
    .post(`/api/articles/${id}`, {elem, views: true})
    .then(res=>{
        dispatch({
            type: 'VIEW_NEWS',
            id: id,
            views: res.data.views,
            ip: res.data.userIpViews
         })

        console.log(res.data,"res.data like")
        })
        .catch(err =>
            console.log(err))
    };

export const likeNews = ( id, elem) => (dispatch) =>{
    axios
    .post(`/api/articles/${id}`, {elem, likes: true})

    .then(res=>{
        console.log(res.data,"res")
      return  dispatch({
            type: 'LIKE_NEWS',
            id: id, 
            likes: res.data.likes,
            ip: res.data.userIpLikes
         }) 
        })
    };


export const searchNews = (text) => ({type: 'SEARCH_NEWS', payload: text});

export const addVisitor = (visitors) => (dispatch) => {


    axios.post('/api/visitors/', {visitors})
    .then(res => {
        if(!res.data){

            return dispatch({
                type: 'ADD_VISITOR',
                payload: []
            })
        } 

        return dispatch({
            type: 'ADD_VISITOR',
            payload: res.data.visitorsIp
        })
    })
}
