import React, {Component} from 'react';
import './article-full.css';
import store from '../../../store';
import {Link} from 'react-router-dom';
import * as actions from '../../../actions';
import {connect} from 'react-redux';

class  ArticleFull extends Component{
    
    likeNews = id => {
        this.props.likeNews(id)

    }

    render(){

        const state = store.getState();
        const {itemId, } = this.props;

        const article = state.articles.map((element, index) => {
        
        if(index === Number(itemId)){
            return (
                <div key={index} className ="article-full-wraper bg-dark" >
                    <Link to="/"><div className="article-full-back" ></div></Link>
                    <div className="article-full bg-white ">
                        <Link to='/'>
                            <button type ="button" className ="close" >&times;</button>
                        </Link>
                        <div className ="article-footer-img"><img src = {element.urlToImage} alt=""></img></div>
                        <div className ="article-title-full text-warning">{element.title}</div>
                        <div className ="article-description-full text-success">{element.description}</div>
                        <div className ="article-content">{element.content}</div>
                        <div className ="article-footer d-flex">
                            <div className ="article-footer-author">{element.author}</div>
                            <div className ="article-footer-views">Views: {element.views}</div>
                            <div className ="article-footer-likes">
                                <button type="button" className="likes-button" onClick={this.likeNews.bind(this, index)} >some button</button>
                                likes: {element.likes}
                            </div>
                        </div>
                    </div>
                </div> 
            );
        };
        return null;
        });
        return article;
    }
}

const mapStateToProps = (articles) => {
    return {
       state: articles
    };
};
    
export default connect(mapStateToProps, actions)(ArticleFull);
