import React, {Component} from 'react';

import {connect} from 'react-redux';
import {Link} from 'react-router-dom';

import * as actions from '../../../actions';
import { fetchPosts} from '../../../actions/actions';

import './articles.css';

class  Articles extends Component {
    // console.log(likeNews)
    
    // = ({state, viewNews, likeNews}) =>


    // if(!visibleItems.length){
    //     return (<div>Nothing Found!</div>)
    // }
    // const articles = visibleItems.map((elem)=>{
        
    //     let img = null;
    //     const id = elem.id
    //     if(elem.urlToImage){
    //         img =<img src = {elem.urlToImage} alt=""></img>;
    //     }
    //     if(elem.content == null){
    //         elem.content = <a href={elem.url}>{elem.url}</a>;
    //      };
    // 
    // console.log(Object.keys(state),"stateKeys")
    // likeNews = id => {
    //     this.props.likeNews(id)

    // }

    // viewNews = id => {
    //     this.props.viewNews(id)

    // }


    onSearch = (search)=>{
            this.setState({search});
        };
    
        search(articles, search){
            if(!articles){
                return articles;
            };
            if(search.length === 0){
                return articles;
            };
            const newArr = articles.filter((elem)=>{
                if(elem.title || elem.description ){
                    if(typeof(elem.content) === "string"){
                      return  elem.content.toLowerCase().indexOf(search.toLowerCase()) > -1;
                    };
                  // не получается подсветка найденного текста
                return  elem.title.toLowerCase().indexOf(search.toLowerCase()) > -1 || elem.description.toLowerCase().indexOf(search.toLowerCase()) > -1 
                };
                return false
            });
            return newArr
        };

    render(){

        const {state, likeNews, viewNews} = this.props;
        console.log(state)
        const article =  state.articles.map((elem, index) => {

            let img = null;

            if(elem.urlToImage){
                img =<img src = {elem.urlToImage} alt=""></img>;
            };

            const artic = ( 
                <div key={elem.id} className ="article border border-primary d-flex flex-column align-item-between">
                    <div className ="article-title"><p className =" h1 text-primary font-weight-bold ">{elem.title}</p></div>
                    <div className ="article-description text-success">{elem.description}</div>
                    <div className ="article-content">{elem.content}</div>
                    <Link to={`article/${index}`}> <button type ="button" className ="article-show-content" onClick={viewNews.bind(this, index)}>more>>></button></Link>
                    <div className ="article-footer d-flex">
                        <div className ="article-footer-img">{img}</div>
                        <div className ="article-footer-author">{elem.author}</div>
                        <div className ="article-footer-views">Views: {elem.views}</div>
                        <div className ="article-footer-likes">
                        <button type="button" className="likes-button" onClick={likeNews.bind(this, index)}>some button</button>
                        likes: {elem.likes}</div>
                    </div>
                </div>
            );

            if(elem.title && elem.title.toLowerCase().indexOf(state.search.toLowerCase()) > -1){
                return artic
            }else if (elem.description && elem.description.toLowerCase().indexOf(state.search.toLowerCase()) > -1){
                return artic
            }else
            //  || )
             {


            return artic
                // <div key={elem.id} className ="article border border-primary d-flex flex-column align-item-between">
                //     <div className ="article-title"><p className =" h1 text-primary font-weight-bold ">{elem.title}</p></div>
                //     <div className ="article-description text-success">{elem.description}</div>
                //     <div className ="article-content">{elem.content}</div>
                //     <Link to={`article/${index}`}> <button type ="button" className ="article-show-content" onClick={viewNews.bind(this, index)}>more>>></button></Link>
                //     <div className ="article-footer d-flex">
                //         <div className ="article-footer-img">{img}</div>
                //         <div className ="article-footer-author">{elem.author}</div>
                //         <div className ="article-footer-views">Views: {elem.views}</div>
                //         <div className ="article-footer-likes">
                //         <button type="button" className="likes-button" onClick={likeNews.bind(this, index)}>some button</button>
                //         likes: {elem.likes}</div>
                //     </div>
                // </div>
            
            }
        });

    return ( [article] );
    };
};

fetchPosts(); // change it!!! it's bad practice!!
const mapStateToProps = (articles) => {
    return {
       state: articles
    };
};

export default connect(mapStateToProps, actions)(Articles);
