import store from '../store'
import axios from 'axios';


export const addNews = () => {
    axios
    .get('/api/items')
    .then(res=> {
        console.log(res.data,"res.data")
      store.dispatch({
        type: 'ADD_NEWS',
        payload: res.data,
        loaded: true 
        });
    })
    .catch(err =>
        console.log(err))

 };


export const viewNews = (id, views) => (dispatch) =>{
    debugger
    dispatch({
        type: 'VIEW_NEWS',
        id: id
     })
    axios
    .post(`/api/items/${id}`, {views: ++views})
// setItemLikes(id, index)
    .then(res=>{

        console.log(res.data,"res.data like")
        })
        .catch(err =>
