import axios from 'axios';

export const addNews = () => (dispatch) =>{
    axios
    .get('/api/items')
    .then(res=> {
        console.log(res.data,"res.data")
      dispatch({
        type: 'ADD_NEWS',
        payload: res.data,
        loaded: true 
        });
    })
    .catch(err =>
        console.log(err))
 };

export const viewNews = (id, elem) => (dispatch) =>{


    axios
    .post(`/api/items/${id}`, {elem, views: true})
    .then(res=>{
        dispatch({
            type: 'VIEW_NEWS',
            id: id,
            views: res.data.vie,
            ip: res.data.userIpViews
         })

        console.log(res.data,"res.data like")
        })
        .catch(err =>
            console.log(err))
    };

export const likeNews = ( id, elem) => (dispatch) =>{
    debugger
    axios
    .post(`/api/items/${id}`, {elem, likes: true})

    .then(res=>{
        console.log(res.data,"res")
      return  dispatch({
            type: 'LIKE_NEWS',
            id: id,
            likes: res.data.likes,
            ip: res.data.userIpLikes
         })
        })
    };


export const searchNews = (text) => ({type: 'SEARCH_NEWS', payload: text});
