     return{
         type: 'ADD_NEWS',
         payload: news
     };
 };

export const viewNews = () => ({type: 'VIEW_NEWS'}); //set key = [id].views++

export const likeNews = (id) => ({type: 'LIKE_NEWS', payload: 0});




export function fetchPosts() {

