import NewsService from '../services/api-service';
import store from '../store'
const newsService = new NewsService();


export const addNews = (news) => {
     return{
         type: 'ADD_NEWS',
         payload: news
     };
 };

export const viewNews = () => ({type: 'VIEW_NEWS'}); //set key = [id].views++

export const likeNews = (id) => ({type: 'LIKE_NEWS', payload: id});

export const hideNews = () => ({type: 'HIDE_NEWS'});



export function fetchPosts() {

    return newsService.getNews().then(news => store.dispatch(addNews(news)))

}
export function addViews(id) {

    let key = "[id].views++"
    console.log(key,"key")
    store.dispatch(viewNews(key))
}
