import axios from 'axios';

export const addNews = () => (dispatch) =>{
    axios
    .get('/api/items')
    .then(res=> {
        console.log(res.data,"res.data")
      dispatch({
        type: 'ADD_NEWS',
        payload: res.data,
        loaded: true 
        });
    })
    .catch(err =>
        console.log(err))
 };

export const viewNews = (id, views) => (dispatch) =>{

    dispatch({
        type: 'VIEW_NEWS',
        id: id
     })
    axios
    .post(`/api/items/${id}`, {views: ++views})
    .then(res=>{

        console.log(res.data,"res.data like")
        })
        .catch(err =>
            console.log(err))
    };

export const likeNews = ( id, elem) => (dispatch) =>{
    debugger
    axios
    .post(`/api/items/${id}`, {elem, likes: true})

    .then(res=>{

      return  dispatch({
            type: 'LIKE_NEWS',
            id: id
         })
        })
    };


export const searchNews = (text) => ({type: 'SEARCH_NEWS', payload: text});
